//CRUD METHODS


/**
 * BlockService.create
 *   @description CRUD ACTION create
 *
 */
app.post(properties.api + '/blocks', function (req, res) {
	obj = new db_RapiDevKit_db.Block(req.body);
	obj.save(function (err) {
		if (err) return handleError(err, res);
		res.send(obj);
	});
});

/**
 * BlockService.delete
 *   @description CRUD ACTION delete
 *   @param ObjectId id Id
 *
 */
app['delete'](properties.api + '/blocks/:id', function (req, res) {
	db_RapiDevKit_db.Block.findByIdAndRemove(req.params.id, function (err) {
		  if (err) return handleError(err, res);
		  res.send(err);
	});
});

/**
 * BlockService.findByActyve
 *   @description CRUD ACTION findByActyve
 *   @param Objectid key Id of the resource Actyve to search
 *
 */
app.get(properties.api + '/blocks/findByActyve/:key', function (req, res) {

	db_RapiDevKit_db.Block.find({ 'Actyve' : req.params.key}).exec(function (err, list) {
		if (err) return handleError(err, res);
		res.send(list);
	});
	
});

