/**
 * BlockService.get
 *   @description CRUD ACTION get
 *   @param ObjectId id Id 
 *
 */
app.get(properties.api + '/blocks/:id', function (req, res) {
	db_RapiDevKit_db.Block.findOne({_id:req.params.id}).exec(function (err, obj) {
		if (err) return handleError(err, res);
		res.send(obj);
	});
});

/**
 * BlockService.getBlockType
 *   @description CRUD ACTION getBlockType
 *   @param Objectid id ID of Block from BlockType
 *
 */
app.get(properties.api + '/blocks/:id/getBlockType', function (req, res) {

	db_RapiDevKit_db.Block.findOne({ _id : req.params.id})
	.populate("BlockType")
	.exec(function (err, obj) {
		if (err) return handleError(err, res);
		
		if(obj)
			res.send(obj.BlockType);
		else
			res.send();
	});
	
});

/**
 * BlockService.list
 *   @description CRUD ACTION list
 *
 */
app.get(properties.api + '/blocks', function (req, res) {
	db_RapiDevKit_db.Block.find().exec(function (err, list) {
		if (err) return handleError(err, res);
		res.send(list);
	});
});

/**
 * BlockService.update
 *   @description CRUD ACTION update
 *   @param ObjectId id Id
 *
 */
app.post(properties.api + '/blocks/:id', function (req, res) {
	db_RapiDevKit_db.Block.findByIdAndUpdate(req.params.id, req.body, {'new': true}, function (err, obj) {
		if (err) return handleError(err, res);
		res.send(obj);
	});
});


